import { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AttendanceAnalysis = () => {
  const { t } = useTranslation();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [attendanceData, setAttendanceData] = useState([]);
  const [students, setStudents] = useState([]);
  const [summary, setSummary] = useState({
    totalStudents: 0,
    averageAttendance: 0,
    highestAttendanceDay: '',
    lowestAttendanceDay: ''
  });

  const currentDate = new Date();
  const currentMonth = currentDate.getMonth() + 1; // JS months are 0-indexed
  const currentYear = currentDate.getFullYear();

  const [filters, setFilters] = useState({
    month: currentMonth,
    year: currentYear,
    classYear: '',
    classCode: '',
    studentId: '',
    searchTerm: ''
  });

  useEffect(() => {
    fetchStudents();
    fetchAttendanceData();
  }, [filters.month, filters.year, filters.classYear, filters.classCode, filters.studentId]);

  const fetchStudents = async () => {
    try {
      // Prepare query parameters for student filtering
      const queryParams = new URLSearchParams();
      
      if (filters.classYear) queryParams.append('classYear', filters.classYear);
      if (filters.classCode) queryParams.append('classCode', filters.classCode);
      if (filters.searchTerm) queryParams.append('search', filters.searchTerm);
      
      const response = await axios.get(`/api/students?${queryParams.toString()}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      setStudents(response.data.data || []);
      
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  // Get all Sundays in the selected month/year
  const getSundaysInMonth = (month, year) => {
    const sundays = [];
    const date = new Date(year, month - 1, 1);
    
    // Adjust to the first Sunday
    while (date.getDay() !== 0) {
      date.setDate(date.getDate() + 1);
    }
    
    // Get all Sundays in the month
    while (date.getMonth() === month - 1) {
      sundays.push(new Date(date));
      date.setDate(date.getDate() + 7);
    }
    
    return sundays;
  };
  
  const fetchAttendanceData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Prepare query parameters
      const queryParams = new URLSearchParams();
      queryParams.append('month', filters.month);
      queryParams.append('year', filters.year);
      if (filters.classYear) queryParams.append('classYear', filters.classYear);
      if (filters.classCode) queryParams.append('classCode', filters.classCode);
      if (filters.studentId) queryParams.append('studentId', filters.studentId);
      
      // In a real application, you would call your API here
      const response = await axios.get(`/api/attendance/analysis?${queryParams.toString()}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      // For development, generate mock data if API doesn't exist yet
      if (!response.data || !response.data.data) {
        const sundays = getSundaysInMonth(filters.month, filters.year);
        const mockData = generateMockData(sundays, filters.month, filters.year, students.length || 35);
        setAttendanceData(mockData.dailyData);
        setSummary(mockData.summary);
        setLoading(false);
        return;
      }

      setAttendanceData(response.data.dailyData || []);
      setSummary(response.data.summary || {
        totalStudents: 0,
        averageAttendance: 0,
        highestAttendanceDay: '',
        lowestAttendanceDay: ''
      });
      
    } catch (error) {
      console.error('Error fetching attendance data:', error);
      setError(error.response?.data?.message || 'Failed to load attendance data');
      
      // Generate mock data for development
      const sundays = getSundaysInMonth(filters.month, filters.year);
      const mockData = generateMockData(sundays, filters.month, filters.year, students.length || 35);
      setAttendanceData(mockData.dailyData);
      setSummary(mockData.summary);
      
    } finally {
      setLoading(false);
    }
  };

  // Function to generate realistic mock data for Sunday-only classes
  const generateMockData = (sundays, month, year, totalStudentCount) => {
    const totalStudents = totalStudentCount;
    const dailyData = [];
    let highestAttendance = 0;
    let lowestAttendance = totalStudents;
    let highestDay = '';
    let lowestDay = '';
    let totalAttendance = 0;
    
    // Initialize the array with all days in the month
    const daysInMonth = new Date(year, month, 0).getDate();
    
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month - 1, day);
      const dateString = date.toISOString().split('T')[0];
      
      // Check if this date is a Sunday
      const isSunday = date.getDay() === 0;
      
      if (isSunday) {
        // Generate attendance for Sundays
        const absentees = Math.floor(Math.random() * 10);
        const lateStudents = Math.floor(Math.random() * 5);
        const present = Math.max(0, totalStudents - absentees - lateStudents);
        const attendanceRate = totalStudents > 0 ? ((present + lateStudents) / totalStudents) * 100 : 0;
        
        totalAttendance += attendanceRate;
        
        if (present + lateStudents > highestAttendance) {
          highestAttendance = present + lateStudents;
          highestDay = dateString;
        }
        
        if (present + lateStudents < lowestAttendance && totalStudents > 0) {
          lowestAttendance = present + lateStudents;
          lowestDay = dateString;
        }
        
        dailyData.push({
          date: dateString,
          present,
          absent: absentees,
          late: lateStudents,
          attendanceRate: parseFloat(attendanceRate.toFixed(2)),
          isSunday: true,
          isClassDay: true
        });
      } else {
        // Non-Sunday days - no class day
        dailyData.push({
          date: dateString,
          present: 0,
          absent: 0,
          late: 0,
          attendanceRate: 0,
          isSunday: false,
          isClassDay: false
        });
      }
    }
    
    const sundaysCount = sundays.length;
    const averageAttendance = sundaysCount > 0 
      ? parseFloat((totalAttendance / sundaysCount).toFixed(2)) 
      : 0;
    
    return {
      dailyData,
      summary: {
        totalStudents,
        averageAttendance,
        highestAttendanceDay: highestDay,
        lowestAttendanceDay: lowestDay
      }
    };
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleStudentSelect = (e) => {
