                </div>
              </div>
            ) : (
              <div className="text-center p-6 text-gray-500 dark:text-gray-400">
                {t('admin.students.studentNotFound')}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AttendanceAnalysis;
