import React from 'react';
import { Link } from 'react-router-dom';

const ActionButton = ({ type, to, onClick, label }) => {
  // Get the appropriate icon based on type
  const getIcon = () => {
    switch(type) {
      case 'view':
        return (
          <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
          </svg>
        );
      case 'edit':
        return (
          <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
          </svg>
        );
      case 'delete':
        return (
          <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
          </svg>
        );
      default:
        return null;
    }
  };
  
  // Get the appropriate color styles based on type
  const getButtonStyle = () => {
    switch(type) {
      case 'view':
        return 'text-blue-600 hover:bg-blue-50 hover:text-blue-800 border-blue-200';
      case 'edit':
        return 'text-indigo-600 hover:bg-indigo-50 hover:text-indigo-800 border-indigo-200';
      case 'delete':
        return 'text-red-600 hover:bg-red-50 hover:text-red-800 border-red-200';
      default:
        return '';
    }
  };
  
  const buttonClasses = `flex items-center px-3 py-1.5 rounded-md border transition-all transform hover:scale-105 ${getButtonStyle()}`;
  
  // Render either a Link or a button based on if "to" prop is provided
  if (to) {
    return (
      <Link to={to} className={buttonClasses} title={label}>
        {getIcon()}
        <span className="ml-1.5">{label}</span>
      </Link>
    );
  }
  
  return (
    <button onClick={onClick} className={buttonClasses} title={label}>
      {getIcon()}
      <span className="ml-1.5">{label}</span>
    </button>
  );
};

export default ActionButton;
