import { useState, useEffect, useCallback } from 'react';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid } from 'recharts';

const AttendanceAnalysis = () => {
  const { t } = useTranslation();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [attendanceData, setAttendanceData] = useState([]);
  const [students, setStudents] = useState([]);
  const [summary, setSummary] = useState({
    totalStudents: 0,
    averageAttendance: 0,
    highestAttendanceDay: '',
    lowestAttendanceDay: '',
  });

  const currentDate = new Date();
  const currentMonth = currentDate.getMonth() + 1;
  const currentYear = currentDate.getFullYear();

  const [filters, setFilters] = useState({
    month: currentMonth,
    year: currentYear,
    classCode: '',
    searchTerm: '',
    studentId: ''
  });

  // Helper: Get all Sundays in a month
  const getSundaysInMonth = (month, year) => {
    const sundays = [];
    const date = new Date(year, month - 1, 1);
    while (date.getDay() !== 0) date.setDate(date.getDate() + 1);
    while (date.getMonth() === month - 1) {
      sundays.push(new Date(date));
      date.setDate(date.getDate() + 7);
    }
    return sundays;
  };

  // Fetch students (mock fallback)
  const fetchStudents = useCallback(async () => {
    try {
      const res = await axios.get('/api/students', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setStudents(res.data.data || []);
      return res.data.data || [];
    } catch {
      // Mock fallback
      const mock = Array(15).fill(0).map((_, i) => ({
        _id: `student-${i+1}`,
        studentId: `SATHI-${filters.year}-A-${String(i+1).padStart(3, '0')}`,
        name: { en: `Student ${i+1}` }
      }));
      setStudents(mock);
      return mock;
    }
  }, [filters.year]);

  // Generate mock attendance data
  const generateMockData = (studentsList) => {
    if (!Array.isArray(studentsList)) return { daily: [], summary: {} };
    const sundays = getSundaysInMonth(filters.month, filters.year);
    const daily = sundays.map(sunday => {
      const present = Math.floor(Math.random() * studentsList.length * 0.8) + Math.floor(studentsList.length * 0.2);
      const absent = studentsList.length - present;
      const late = Math.floor(Math.random() * (present / 4));
      return {
        date: sunday.toISOString().split('T')[0],
        present,
        absent,
        late,
        attendanceRate: studentsList.length ? ((present / studentsList.length) * 100).toFixed(1) : 0
      };
    });
    // Summary
    const avg = daily.length
      ? (daily.reduce((sum, d) => sum + Number(d.attendanceRate), 0) / daily.length).toFixed(1)
      : 0;
    const highest = daily.reduce((a, b) => (a.present > b.present ? a : b), { present: 0 });
    const lowest = daily.reduce((a, b) => (a.present < b.present ? a : b), { present: studentsList.length });
    return {
      daily,
      summary: {
        totalStudents: studentsList.length,
        averageAttendance: avg,
        highestAttendanceDay: highest.date
          ? `${new Date(highest.date).toLocaleDateString()} (${highest.present} ${t('admin.students')})`
          : t('admin.attendance.noData'),
        lowestAttendanceDay: lowest.date
          ? `${new Date(lowest.date).toLocaleDateString()} (${lowest.present} ${t('admin.students')})`
          : t('admin.attendance.noData')
      }
    };
  };

  // Fetch attendance (mock fallback)
  const fetchAttendance = useCallback(async (studentsList) => {
    // Here you would fetch real data, but we'll use mock for demo
    const mock = generateMockData(studentsList);
    setAttendanceData(mock.daily);
    setSummary(mock.summary);
  }, [filters.month, filters.year, t]);

  // Main effect
  useEffect(() => {
    setLoading(true);
    setError(null);
    (async () => {
      try {
        const studentsList = await fetchStudents();
        await fetchAttendance(studentsList);
      } catch (e) {
        setError(t('admin.attendance.errorLoading'));
      } finally {
        setLoading(false);
      }
    })();
  }, [fetchStudents, fetchAttendance, filters, t]);

  // Filtered students for dropdown
  const filteredStudents = students.filter(student =>
    !filters.searchTerm ||
    student.name.en.toLowerCase().includes(filters.searchTerm.toLowerCase()) ||
    student.studentId.toLowerCase().includes(filters.searchTerm.toLowerCase())
  );

  // Export CSV
  const handleExportCSV = () => {
    if (!attendanceData.length) return;
    const headers = ["Date", "Present", "Absent", "Late", "Attendance Rate"];
    const rows = attendanceData.map(day =>
      [new Date(day.date).toLocaleDateString(), day.present, day.absent, day.late, `${day.attendanceRate}%`]
    );
    const csv = [headers, ...rows].map(r => r.join(",")).join("\n");
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `attendance-${filters.year}-${String(filters.month).padStart(2, '0')}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  return (
    <div className="space-y-6">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4 md:mb-0">
            {t('admin.attendance.analysis')}
          </h2>
          <button
            onClick={handleExportCSV}
            className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors"
            disabled={!attendanceData.length}
          >
            {t('admin.export.csv')}
          </button>
        </div>

        {/* Filters */}
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.month')}
            </label>
            <select
              name="month"
              value={filters.month}
              onChange={e => setFilters(f => ({ ...f, month: Number(e.target.value) }))}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            >
              {months.map((month, idx) => (
                <option key={idx} value={idx + 1}>
                  {t(`admin.months.${month.toLowerCase()}`)}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.year')}
            </label>
            <select
              name="year"
              value={filters.year}
              onChange={e => setFilters(f => ({ ...f, year: Number(e.target.value) }))}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            >
              {[...Array(5)].map((_, i) => (
                <option key={i} value={currentYear - i}>
                  {currentYear - i}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.class')}
            </label>
            <select
              name="classCode"
              value={filters.classCode}
              onChange={e => setFilters(f => ({ ...f, classCode: e.target.value }))}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            >
              <option value="">{t('admin.filters.all')}</option>
              <option value="ADH">Adhiṭṭhāna</option>
              <option value="MET">Mettā</option>
              <option value="KHA">Khanti</option>
              <option value="NEK">Nekkhamma</option>
            </select>
          </div>
          <div className="md:col-span-2">
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.search')}
            </label>
            <input
              type="text"
              value={filters.searchTerm}
              onChange={e => setFilters(f => ({ ...f, searchTerm: e.target.value }))}
              placeholder={t('admin.attendance.searchPlaceholder')}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            />
          </div>
        </div>

        {/* Student Filter */}
        {filteredStudents.length > 0 && (
          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.students.selectStudent')}
            </label>
            <select
              value={filters.studentId}
              onChange={e => setFilters(f => ({ ...f, studentId: e.target.value }))}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            >
              <option value="">{t('admin.filters.all')}</option>
              {filteredStudents.map(student => (
                <option key={student._id} value={student._id}>
                  {student.studentId} - {student.name.en}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* Error/Loading */}
        {error && (
          <div className="p-3 mb-4 bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300 rounded">
            {error}
          </div>
        )}
        {loading && (
          <div className="flex justify-center items-center p-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
          </div>
        )}

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div className="bg-blue-50 dark:bg-blue-900 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.totalStudents')}
            </h3>
            <p className="text-lg font-semibold text-blue-800 dark:text-blue-200">
              {summary.totalStudents}
            </p>
          </div>
          <div className="bg-green-50 dark:bg-green-900 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.averageAttendance')}
            </h3>
            <p className="text-lg font-semibold text-green-800 dark:text-green-200">
              {summary.averageAttendance}%
            </p>
          </div>
          <div className="bg-purple-50 dark:bg-purple-900 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.highestAttendance')}
            </h3>
            <p className="text-lg font-semibold text-purple-800 dark:text-purple-200">
              {summary.highestAttendanceDay}
            </p>
          </div>
          <div className="bg-yellow-50 dark:bg-yellow-900 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.lowestAttendance')}
            </h3>
            <p className="text-lg font-semibold text-yellow-800 dark:text-yellow-200">
              {summary.lowestAttendanceDay}
            </p>
          </div>
        </div>

        {/* Attendance Chart */}
        <div className="mb-8">
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
            {t('admin.attendance.trend')}
          </h3>
          <div style={{ width: '100%', height: 250 }}>
            <ResponsiveContainer>
              <LineChart data={attendanceData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" tickFormatter={d => new Date(d).getDate()} />
                <YAxis domain={[0, 100]} tickFormatter={v => `${v}%`} />
                <Tooltip formatter={v => `${v}%`} labelFormatter={d => new Date(d).toLocaleDateString()} />
                <Line type="monotone" dataKey="attendanceRate" stroke="#10b981" strokeWidth={2} dot />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Sundays Calendar */}
        <div className="mb-8">
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
            {t('admin.attendance.sundays')} - {t(`admin.months.${months[filters.month-1].toLowerCase()}`)} {filters.year}
          </h3>
          <div className="flex flex-wrap gap-2">
            {getSundaysInMonth(filters.month, filters.year).map((sunday) => {
              const dateStr = sunday.toISOString().split('T')[0];
              const day = attendanceData.find(d => d.date === dateStr);
              const attendanceRate = day ? day.attendanceRate : '0';
              return (
                <div key={dateStr} className="bg-white dark:bg-gray-700 rounded-lg shadow p-4 w-32">
                  <div className="text-center">
                    <div className="text-sm font-medium">{sunday.getDate()}</div>
                    <div className="text-xs text-gray-500">Sunday</div>
                    <div className="mt-2">
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div
                          className="bg-green-600 h-2 rounded-full"
                          style={{ width: `${attendanceRate}%` }}
                        ></div>
                      </div>
                    </div>
                    <div className="mt-1 text-sm font-medium">
                      {attendanceRate}%
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Daily Breakdown Table */}
        <div>
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
            {t('admin.attendance.dailyBreakdown')}
          </h3>
          {!loading && attendanceData.length > 0 ? (
            <div className="overflow-x-auto rounded-lg shadow">
              <table className="min-w-full bg-white dark:bg-gray-800">
                <thead className="bg-gray-100 dark:bg-gray-700">
                  <tr>
                    <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">
                      {t('admin.attendance.date')}
                    </th>
                    <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">
                      {t('admin.attendance.present')}
                    </th>
                    <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">
                      {t('admin.attendance.absent')}
                    </th>
                    <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">
                      {t('admin.attendance.late')}
                    </th>
                    <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">
                      {t('admin.attendance.attendanceRate')}
                    </th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
                  {attendanceData.map(day => (
                    <tr key={day.date} className="hover:bg-gray-50 dark:hover:bg-gray-900">
                      <td className="px-4 py-2 text-sm text-gray-900 dark:text-white">
                        {new Date(day.date).toLocaleDateString()}
                        <span className="ml-2 text-xs text-blue-600 dark:text-blue-400">
                          {t('admin.attendance.sunday')}
                        </span>
                      </td>
                      <td className="px-4 py-2 text-sm text-green-600 dark:text-green-400">
                        {day.present}
                      </td>
                      <td className="px-4 py-2 text-sm text-red-600 dark:text-red-400">
                        {day.absent}
                      </td>
                      <td className="px-4 py-2 text-sm text-yellow-600 dark:text-yellow-400">
                        {day.late}
                      </td>
                      <td className="px-4 py-2 text-sm">
                        <div className="flex items-center">
                          <div className="w-16 bg-gray-200 rounded-full h-2.5 mr-2">
                            <div
                              className="bg-green-600 h-2.5 rounded-full"
                              style={{ width: `${day.attendanceRate}%` }}
                            ></div>
                          </div>
                          <span>{day.attendanceRate}%</span>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : !loading ? (
            <div className="text-center p-6 text-gray-500 dark:text-gray-400 bg-gray-50 dark:bg-gray-900 rounded">
              {t('admin.attendance.noDataFound')}
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
};

export default AttendanceAnalysis;
