import { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import axios from 'axios';

const AttendanceAnalysis = () => {
  const { t } = useTranslation();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [attendanceData, setAttendanceData] = useState([]);
  const [summary, setSummary] = useState({
    totalStudents: 0,
    averageAttendance: 0,
    highestAttendanceDay: '',
    lowestAttendanceDay: ''
  });

  const currentDate = new Date();
  const currentMonth = currentDate.getMonth() + 1; // JS months are 0-indexed
  const currentYear = currentDate.getFullYear();

  const [filters, setFilters] = useState({
    month: currentMonth,
    year: currentYear,
    classYear: '',
    classCode: ''
  });

  // Generate labels for days of the month
  const getDaysInMonth = (month, year) => {
    return new Date(year, month, 0).getDate();
  };

  const daysInSelectedMonth = getDaysInMonth(filters.month, filters.year);
  const labels = Array.from({ length: daysInSelectedMonth }, (_, i) => i + 1);

  useEffect(() => {
    fetchAttendanceData();
  }, [filters]);

  const fetchAttendanceData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Prepare query parameters
      const queryParams = new URLSearchParams();
      queryParams.append('month', filters.month);
      queryParams.append('year', filters.year);
      if (filters.classYear) queryParams.append('classYear', filters.classYear);
      if (filters.classCode) queryParams.append('classCode', filters.classCode);
      
      // In a real application, you would call your API here
      const response = await axios.get(`/api/attendance/analysis?${queryParams.toString()}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      // For development, generate mock data if API doesn't exist yet
      if (!response.data || !response.data.data) {
        const mockData = generateMockData(filters.month, filters.year, daysInSelectedMonth);
        setAttendanceData(mockData.dailyData);
        setSummary(mockData.summary);
        setLoading(false);
        return;
      }

      setAttendanceData(response.data.dailyData || []);
      setSummary(response.data.summary || {
        totalStudents: 0,
        averageAttendance: 0,
        highestAttendanceDay: '',
        lowestAttendanceDay: ''
      });
      
    } catch (error) {
      console.error('Error fetching attendance data:', error);
      setError(error.response?.data?.message || 'Failed to load attendance data');
      
      // Generate mock data for development
      const mockData = generateMockData(filters.month, filters.year, daysInSelectedMonth);
      setAttendanceData(mockData.dailyData);
      setSummary(mockData.summary);
      
    } finally {
      setLoading(false);
    }
  };

  // Function to generate realistic mock data for development
  const generateMockData = (month, year, daysInMonth) => {
    const totalStudents = 35 + Math.floor(Math.random() * 10);
    const dailyData = [];
    let highestAttendance = 0;
    let lowestAttendance = totalStudents;
    let highestDay = '';
    let lowestDay = '';
    let totalAttendance = 0;
    
    for (let day = 1; day <= daysInMonth; day++) {
      // Skip weekends (Saturday = 6, Sunday = 0)
      const date = new Date(year, month - 1, day);
      const dayOfWeek = date.getDay();
      
      if (dayOfWeek === 0 || dayOfWeek === 6) {
        // Weekend - no attendance
        dailyData.push({
          date: `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`,
          present: 0,
          absent: 0,
          late: 0,
          attendanceRate: 0,
          isWeekend: true
        });
        continue;
      }
      
      // Random attendance for weekdays
      const absentees = Math.floor(Math.random() * 10);
      const lateStudents = Math.floor(Math.random() * 5);
      const present = Math.max(0, totalStudents - absentees - lateStudents);
      const attendanceRate = ((present + lateStudents) / totalStudents) * 100;
      
      totalAttendance += attendanceRate;
      
      if (present + lateStudents > highestAttendance) {
        highestAttendance = present + lateStudents;
        highestDay = `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
      }
      
      if (present + lateStudents < lowestAttendance) {
        lowestAttendance = present + lateStudents;
        lowestDay = `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
      }
      
      dailyData.push({
        date: `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`,
        present,
        absent: absentees,
        late: lateStudents,
        attendanceRate: parseFloat(attendanceRate.toFixed(2)),
        isWeekend: false
      });
    }
    
    const weekdaysCount = dailyData.filter(day => !day.isWeekend).length;
    const averageAttendance = weekdaysCount > 0 
      ? parseFloat((totalAttendance / weekdaysCount).toFixed(2)) 
      : 0;
    
    return {
      dailyData,
      summary: {
        totalStudents,
        averageAttendance,
        highestAttendanceDay: highestDay,
        lowestAttendanceDay: lowestDay
      }
    };
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleExportCSV = () => {
    // Function to create CSV content from attendance data
    const headers = "Date,Present,Absent,Late,Attendance Rate\n";
    const rows = attendanceData
      .filter(day => !day.isWeekend)
      .map(day => 
        `${day.date},${day.present},${day.absent},${day.late},${day.attendanceRate}%`
      )
      .join('\n');
    
    const csvContent = headers + rows;
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    
    const link = document.createElement('a');
    link.setAttribute('href', url);
    link.setAttribute('download', `attendance-${filters.year}-${filters.month}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="space-y-6">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4 md:mb-0">
            {t('admin.attendance.monthlyAnalysis')}
          </h2>
          
          <button
            onClick={handleExportCSV}
            className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors"
          >
            {t('admin.export.csv')}
          </button>
        </div>
        
        {/* Filters */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.month')}
            </label>
            <select
              name="month"
              value={filters.month}
              onChange={handleFilterChange}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            >
              <option value="1">{t('admin.months.january')}</option>
              <option value="2">{t('admin.months.february')}</option>
              <option value="3">{t('admin.months.march')}</option>
              <option value="4">{t('admin.months.april')}</option>
              <option value="5">{t('admin.months.may')}</option>
              <option value="6">{t('admin.months.june')}</option>
              <option value="7">{t('admin.months.july')}</option>
              <option value="8">{t('admin.months.august')}</option>
              <option value="9">{t('admin.months.september')}</option>
              <option value="10">{t('admin.months.october')}</option>
              <option value="11">{t('admin.months.november')}</option>
              <option value="12">{t('admin.months.december')}</option>
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.attendance.year')}
            </label>
            <input
              type="text"
              name="year"
              value={filters.year}
              onChange={handleFilterChange}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.students.classYear')}
            </label>
            <input
              type="text"
              name="classYear"
              value={filters.classYear}
              onChange={handleFilterChange}
              placeholder={t('admin.filters.all')}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              {t('admin.students.classCode')}
            </label>
            <select
              name="classCode"
              value={filters.classCode}
              onChange={handleFilterChange}
              className="w-full px-3 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300 dark:bg-gray-700 dark:text-white dark:border-gray-600"
            >
              <option value="">{t('admin.filters.all')}</option>
              <option value="A">A</option>
              <option value="B">B</option>
              <option value="C">C</option>
              <option value="D">D</option>
            </select>
          </div>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div className="bg-blue-50 dark:bg-blue-900 p-4 rounded-lg">
            <h3 className="text-blue-700 dark:text-blue-300 text-sm font-medium mb-1">
              {t('admin.attendance.totalStudents')}
            </h3>
            <p className="text-2xl font-bold text-blue-800 dark:text-blue-200">
              {summary.totalStudents}
            </p>
          </div>
          
          <div className="bg-green-50 dark:bg-green-900 p-4 rounded-lg">
            <h3 className="text-green-700 dark:text-green-300 text-sm font-medium mb-1">
              {t('admin.attendance.averageAttendance')}
            </h3>
            <p className="text-2xl font-bold text-green-800 dark:text-green-200">
              {summary.averageAttendance}%
            </p>
          </div>
          
          <div className="bg-purple-50 dark:bg-purple-900 p-4 rounded-lg">
            <h3 className="text-purple-700 dark:text-purple-300 text-sm font-medium mb-1">
              {t('admin.attendance.highestAttendance')}
            </h3>
            <p className="text-2xl font-bold text-purple-800 dark:text-purple-200">
              {summary.highestAttendanceDay ? new Date(summary.highestAttendanceDay).toLocaleDateString() : '—'}
            </p>
          </div>
          
          <div className="bg-amber-50 dark:bg-amber-900 p-4 rounded-lg">
            <h3 className="text-amber-700 dark:text-amber-300 text-sm font-medium mb-1">
              {t('admin.attendance.lowestAttendance')}
            </h3>
            <p className="text-2xl font-bold text-amber-800 dark:text-amber-200">
              {summary.lowestAttendanceDay ? new Date(summary.lowestAttendanceDay).toLocaleDateString() : '—'}
            </p>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="p-3 mb-4 bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300 rounded">
            {error}
          </div>
        )}
        
        {/* Attendance Visual Chart (simple version for now, could be upgraded with Chart.js) */}
        <div className="mb-6">
          <h3 className="text-base font-medium text-gray-900 dark:text-white mb-3">
            {t('admin.attendance.dailyAttendanceChart')}
          </h3>
          
          {loading ? (
            <div className="flex justify-center items-center h-64 bg-gray-50 dark:bg-gray-900 rounded">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
            </div>
          ) : attendanceData.length === 0 ? (
            <div className="text-center p-8 text-gray-500 dark:text-gray-400 bg-gray-50 dark:bg-gray-900 rounded h-64 flex items-center justify-center">
              {t('admin.attendance.noDataFound')}
            </div>
          ) : (
            <div className="bg-white dark:bg-gray-900 p-4 rounded-lg border border-gray-200 dark:border-gray-700">
              <div className="h-64 flex items-end space-x-1">
                {attendanceData.map((dayData, index) => (
                  <div 
                    key={index} 
                    className="flex-1 flex flex-col items-center"
                    title={`${dayData.date}: ${dayData.present} present, ${dayData.late} late, ${dayData.absent} absent`}
                  >
                    <div className="relative w-full h-full flex flex-col justify-end">
                      {dayData.isWeekend ? (
                        <div className="h-full bg-gray-100 dark:bg-gray-800 rounded-t" />
                      ) : (
                        <>
                          {/* Absent */}
                          <div 
                            style={{ height: `${(dayData.absent / summary.totalStudents) * 100}%` }}
                            className="bg-red-400 dark:bg-red-800 w-full"
                          ></div>
                          
                          {/* Late */}
                          <div 
                            style={{ height: `${(dayData.late / summary.totalStudents) * 100}%` }}
                            className="bg-yellow-400 dark:bg-yellow-600 w-full"
                          ></div>
                          
                          {/* Present */}
                          <div 
                            style={{ height: `${(dayData.present / summary.totalStudents) * 100}%` }}
                            className="bg-green-400 dark:bg-green-600 w-full rounded-t"
                          ></div>
                        </>
                      )}
                    </div>
                    <div className="text-xs mt-1 font-medium text-gray-600 dark:text-gray-400">
                      {labels[index]}
                    </div>
                  </div>
                ))}
              </div>
              
              {/* Legend */}
              <div className="flex justify-center space-x-4 mt-4">
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-green-400 dark:bg-green-600 mr-1"></div>
                  <span className="text-xs text-gray-600 dark:text-gray-400">{t('admin.attendance.present')}</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-yellow-400 dark:bg-yellow-600 mr-1"></div>
                  <span className="text-xs text-gray-600 dark:text-gray-400">{t('admin.attendance.late')}</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-red-400 dark:bg-red-800 mr-1"></div>
                  <span className="text-xs text-gray-600 dark:text-gray-400">{t('admin.attendance.absent')}</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-gray-100 dark:bg-gray-800 mr-1"></div>
                  <span className="text-xs text-gray-600 dark:text-gray-400">{t('admin.attendance.weekend')}</span>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Attendance Table */}
        <div>
          <h3 className="text-base font-medium text-gray-900 dark:text-white mb-3">
            {t('admin.attendance.dailyBreakdown')}
          </h3>
          
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
              <thead className="bg-gray-50 dark:bg-gray-700">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    {t('admin.attendance.date')}
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    {t('admin.attendance.present')}
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    {t('admin.attendance.late')}
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    {t('admin.attendance.absent')}
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    {t('admin.attendance.attendanceRate')}
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
                {loading ? (
                  <tr>
                    <td colSpan="5" className="px-6 py-4 text-center text-sm text-gray-500 dark:text-gray-400">
                      {t('admin.loading')}
                    </td>
                  </tr>
                ) : attendanceData.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="px-6 py-4 text-center text-sm text-gray-500 dark:text-gray-400">
                      {t('admin.attendance.noDataFound')}
                    </td>
                  </tr>
                ) : (
                  attendanceData.map((dayData, index) => (
                    <tr 
                      key={index}
                      className={dayData.isWeekend ? 'bg-gray-50 dark:bg-gray-900 text-gray-400 dark:text-gray-500' : ''}
                    >
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                        {new Date(dayData.date).toLocaleDateString()}
                        {dayData.isWeekend && ` (${t('admin.attendance.weekend')})`}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-center text-gray-500 dark:text-gray-300">
                        {dayData.isWeekend ? '—' : dayData.present}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-center text-gray-500 dark:text-gray-300">
                        {dayData.isWeekend ? '—' : dayData.late}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-center text-gray-500 dark:text-gray-300">
                        {dayData.isWeekend ? '—' : dayData.absent}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-center">
                        {dayData.isWeekend ? (
                          <span className="text-gray-500 dark:text-gray-400">—</span>
                        ) : (
                          <span className={`px-2 py-1 rounded-full text-xs font-medium 
                            ${dayData.attendanceRate >= 90 ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300' :
                              dayData.attendanceRate >= 75 ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300' :
                              'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300'}`}
                          >
                            {dayData.attendanceRate}%
                          </span>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AttendanceAnalysis;
