import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { FaArrowLeft, FaSave } from 'react-icons/fa';
import { useTranslation } from 'react-i18next'; // Import for translations

const EditStudent = () => {
  const { t, i18n } = useTranslation(); // Add translation hook
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const fileInputRef = useRef(null);
  
  // Student form data - removed sinhalaName field
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    ageGroup: '',
    classYear: '',
    classCode: '',
    parentInfo: {
      name: '',
      phone: '',
      email: '',
      address: ''
    },
    emergencyContact: ''
  });
  
  // For image handling
  const [profileImage, setProfileImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [existingImage, setExistingImage] = useState(null);
  
  // Fetch student data
  useEffect(() => {
    const fetchStudentData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/students/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        const student = response.data.data;
        
        // Parse student name into first and last name
        const nameParts = student.name.en.split(' ');
        const lastName = nameParts.pop() || '';
        const firstName = nameParts.join(' ');
        
        setFormData({
          firstName,
          lastName,
          // Removed sinhalaName field
          dateOfBirth: new Date(student.dateOfBirth).toISOString().split('T')[0],
          gender: student.gender || '',
          ageGroup: student.ageGroup || '',
          classYear: student.classYear || '',
          classCode: student.classCode || '',
          parentInfo: {
            name: student.parentInfo?.name?.en || '',
            phone: student.parentInfo?.phone || '',
            email: student.parentInfo?.email || '',
            address: student.parentInfo?.address || ''
          },
          emergencyContact: student.emergencyContact || ''
        });
        
        // Set existing profile image if available
        if (student.profileImage?.url) {
          setExistingImage(student.profileImage.url);
          setImagePreview(student.profileImage.url);
        }
        
      } catch (err) {
        console.error('Error fetching student data:', err);
        setError(err.response?.data?.message || 'Failed to fetch student data');
      } finally {
        setLoading(false);
      }
    };
    
    fetchStudentData();
  }, [id]);
  
  // Calculate age and set age group
  const calculateAgeAndSetAgeGroup = (birthDate) => {
    if (!birthDate) return;
    
    const today = new Date();
    const dob = new Date(birthDate);
    let age = today.getFullYear() - dob.getFullYear();
    
    if (
      today.getMonth() < dob.getMonth() || 
      (today.getMonth() === dob.getMonth() && today.getDate() < dob.getDate())
    ) {
      age--;
    }
    
    let ageGroup;
    if (age >= 0 && age <= 6) {
      ageGroup = '3-6';
      setFormData(prev => ({...prev, classCode: 'ADH'}));
    } else if (age >= 7 && age <= 10) {
      ageGroup = '7-10';
      setFormData(prev => ({...prev, classCode: 'MET'}));
    } else if (age >= 11 && age <= 13) {
      ageGroup = '11-13';
      setFormData(prev => ({...prev, classCode: 'KHA'}));
    } else if (age >= 14) {
      ageGroup = '14+';
      setFormData(prev => ({...prev, classCode: 'NEK'}));
    }
    
    setFormData(prev => ({...prev, ageGroup}));
  };
  
  // Modify the handleChange function
  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'dateOfBirth') {
      calculateAgeAndSetAgeGroup(value);
    }
    
    if (name.startsWith('parentInfo.')) {
      const key = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        parentInfo: {
          ...prev.parentInfo,
          [key]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };
  
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      
      // Validate file type
      const allowedTypes = ['image/jpeg', 'image/png', 'image/gif'];
      if (!allowedTypes.includes(file.type)) {
        setError('Invalid file type. Please use JPG, PNG or GIF format.');
        return;
      }
      
      // Validate file size (max 2MB)
      if (file.size > 2 * 1024 * 1024) {
        setError('Image is too large. Maximum size is 2MB.');
        return;
      }
      
      setProfileImage(file);
      
      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };
  
  const handleRemoveImage = () => {
    setProfileImage(null);
    setImagePreview(null);
    setExistingImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError(null);
    setSuccess(null);
    
    try {
      // Create FormData object for multipart/form-data submission
      const submitData = new FormData();
      
      // Add all form fields
      submitData.append('name[en]', `${formData.firstName} ${formData.lastName}`.trim());
      // Set Sinhala name same as English
      submitData.append('name[si]', `${formData.firstName} ${formData.lastName}`.trim());
      submitData.append('dateOfBirth', formData.dateOfBirth);
      submitData.append('gender', formData.gender);
      submitData.append('ageGroup', formData.ageGroup);
      submitData.append('classYear', formData.classYear);
      submitData.append('classCode', formData.classCode);
      
      // Parent info
      submitData.append('parentInfo[name][en]', formData.parentInfo.name);
      submitData.append('parentInfo[name][si]', formData.parentInfo.name);
      submitData.append('parentInfo[phone]', formData.parentInfo.phone);
      submitData.append('parentInfo[email]', formData.parentInfo.email);
      submitData.append('parentInfo[address]', formData.parentInfo.address);
      
      // Emergency contact
      if (formData.emergencyContact) {
        submitData.append('emergencyContact', formData.emergencyContact);
      }
      
      // Add profile image if changed
      if (profileImage) {
        submitData.append('profileImage', profileImage);
      }
      
      // Flag to indicate if image was removed
      if (existingImage && !imagePreview) {
        submitData.append('removeProfileImage', 'true');
      }
      
      // Send update request
      const response = await axios.put(`/api/students/${id}`, submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (response.data.success) {
        setSuccess(t('admin.students.updateSuccess') || 'Student information updated successfully');
        
        // Navigate back to student details after short delay
        setTimeout(() => {
          navigate(`/admin/students/${id}`);
        }, 2000);
      } else {
        throw new Error(response.data.message || 'Failed to update student');
      }
    } catch (err) {
      console.error('Error updating student:', err);
      setError(err.response?.data?.message || 'Failed to update student information');
    } finally {
      setSubmitting(false);
    }
  };
  
  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 md:px-8">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center">
            <Link
              to={`/admin/students/${id}`}
              className="mr-4 p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700"
            >
              <FaArrowLeft className="text-gray-600 dark:text-gray-300" />
            </Link>
            <h2 className="text-2xl font-semibold text-gray-900 dark:text-white">
              {t('admin.students.edit')} / සිසුවා සංස්කරණය කිරීම
            </h2>
          </div>
        </div>

        {error && (
          <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
            {error}
          </div>
        )}

        {success && (
          <div className="mb-4 p-4 bg-green-100 border border-green-400 text-green-700 rounded">
            {success}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
              {t('admin.students.studentInformation')} / සිසු තොරතුරු
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.firstName')} / මුල් නම
                </label>
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                  required
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.lastName')} / අවසන් නම
                </label>
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                  required
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              {/* Sinhala name field removed */}
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.dateOfBirth')} / උපන් දිනය
                </label>
                <input
                  type="date"
                  name="dateOfBirth"
                  value={formData.dateOfBirth}
                  onChange={handleChange}
                  required
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.gender')} / ස්ත්‍රී පුරුෂ භාවය
                </label>
                <select
                  name="gender"
                  value={formData.gender}
                  onChange={handleChange}
                  required
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                >
                  <option value="">{t('admin.students.selectGender')} / තෝරන්න</option>
                  <option value="M">{t('admin.students.male')} / පුරුෂ</option>
                  <option value="F">{t('admin.students.female')} / ස්ත්‍රී</option>
                  <option value="O">{t('admin.students.other')} / වෙනත්</option>
                </select>
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.ageGroup')} / වයස් කාණ්ඩය
                </label>
                <select
                  name="ageGroup"
                  value={formData.ageGroup}
                  onChange={handleChange}
                  required
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                >
                  <option value="">Select Age Group / වයස් කාණ්ඩය තෝරන්න</option>
                  <option value="3-6">3-6 years (Adhiṭṭhāna)</option>
                  <option value="7-10">7-10 years (Mettā)</option>
                  <option value="11-13">11-13 years (Khanti)</option>
                  <option value="14+">14+ years (Nekkhamma)</option>
                </select>
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.classYear')} / පන්ති වසර
                </label>
                <input
                  type="text"
                  name="classYear"
                  value={formData.classYear}
                  onChange={handleChange}
                  required
                  placeholder="E.g., 2023"
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.classCode')} / පන්තිය
                </label>
                <select
                  name="classCode"
                  value={formData.classCode}
                  onChange={handleChange}
                  required
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                >
                  <option value="">Select Class / පන්තිය තෝරන්න</option>
                  <option value="ADH">Adhiṭṭhāna (අධිඨාන) - White</option>
                  <option value="MET">Mettā (මෙත්තා) - Orange</option>
                  <option value="KHA">Khanti (ඛන්ති) - Yellow</option>
                  <option value="NEK">Nekkhamma (නෙක්කම්ම) - Blue</option>
                </select>
              </div>
            </div>
          </div>
          
          <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
              Profile Image / පින්තූරය
            </h3>
            <div className="flex flex-col md:flex-row md:items-center gap-6">
              <div className="w-32 h-32 relative border rounded overflow-hidden flex items-center justify-center bg-gray-100 dark:bg-gray-700">
                {imagePreview ? (
                  <img 
                    src={imagePreview} 
                    alt="Student Preview" 
                    className="max-w-full max-h-full object-cover"
                  />
                ) : (
                  <div className="text-gray-400 text-6xl">
                    {formData.firstName.charAt(0)}
                  </div>
                )}
              </div>
              
              {/* Upload Controls */}
              <div>
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleImageChange}
                  accept="image/png, image/jpeg, image/gif"
                  className="hidden"
                />
                
                <div className="flex flex-col space-y-2">
                  <button
                    type="button"
                    onClick={() => fileInputRef.current.click()}
                    className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
                  >
                    {imagePreview ? 'Change Image / පින්තූරය වෙනස් කරන්න' : 'Upload Image / පින්තූරය උඩුගත කරන්න'}
                  </button>
                  
                  {imagePreview && (
                    <button
                      type="button"
                      onClick={handleRemoveImage}
                      className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
                    >
                      Remove Image / පින්තූරය ඉවත් කරන්න
                    </button>
                  )}
                </div>
                
                <p className="mt-2 text-xs text-gray-500 dark:text-gray-400">
                  JPG, PNG or GIF. Max 2MB.
                </p>
              </div>
            </div>
          </div>
          
          <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
              {t('admin.students.parentInformation')} / දෙමාපිය තොරතුරු
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.parentName')} / දෙමාපිය නම
                </label>
                <input
                  type="text"
                  name="parentInfo.name"
                  value={formData.parentInfo.name}
                  onChange={handleChange}
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.parentPhone')} / දුරකථන අංකය
                </label>
                <input
                  type="text"
                  name="parentInfo.phone"
                  value={formData.parentInfo.phone}
                  onChange={handleChange}
                  required
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.parentEmail')} / විද්‍යුත් තැපෑල
                </label>
                <input
                  type="email"
                  name="parentInfo.email"
                  value={formData.parentInfo.email}
                  onChange={handleChange}
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.emergencyContact')} / හදිසි ඇමතුම් අංකය
                </label>
                <input
                  type="text"
                  name="emergencyContact"
                  value={formData.emergencyContact}
                  onChange={handleChange}
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              
              <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  {t('admin.students.address')} / ලිපිනය
                </label>
                <textarea
                  name="parentInfo.address"
                  value={formData.parentInfo.address}
                  onChange={handleChange}
                  rows={3}
                  className="block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm placeholder-gray-400 dark:bg-gray-700 dark:text-white focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                ></textarea>
              </div>
            </div>
          </div>
          
          <div className="flex justify-end">
            <Link
              to={`/admin/students/${id}`}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 text-sm rounded-md text-gray-700 dark:text-gray-300 bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700 mr-3"
            >
              Cancel / අවලංගු කරන්න
            </Link>
            <button
              type="submit"
              disabled={submitting}
              className="flex items-center px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors disabled:opacity-50"
            >
              {submitting ? 'Saving... / සුරකිමින්...' : (
                <>
                  <FaSave className="mr-2" />
                  Save Changes / සුරකින්න
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditStudent;
